mapper.update:
update tb_resource
`buildingId`=#{buildingId}, `district`=#{district}, cityName=#{cityName}, streetName=#{streetName}, villageName=#{villageName}, admStreetName=#{admStreetName}, zoneName=#{zoneName}, `floor`=#{floor}, `number`=#{number}, `allPortCount`=#{allPortCount}, `idelPortCount`=#{idelPortCount}, `sceneA`=#{sceneA}, `sceneB`=#{sceneB}, `overlayScene`=#{overlayScene}, longitude=#{longitude}, latitude=#{latitude},
where id=#{id}

mapper.select:
select * from tb_resource where 1=1

    and buildingId=#{buildingId}

order by id desc

select * from tb_resource where buildingId=#{buildingId}
and `floor`=#{floor} and `number`=#{number}
and longitude=#{longitude} and latitude=#{latitude}

select id,allPortCount,idelPortCount,longitude,latitude from tb_resource
where longitude between #{loMin} and #{loMax}
and latitude between #{laMin} and #{laMax}
